var objects = ['shop','amenity','leisure','military'];

function isObject(key) {
  var objectsLength = objects.length;
  for (var i = 0; i < objectsLength; i++) {
    if (objects[i].equals(key)) {
            return true;
    }
  }
  return false;
}

